-- purpose: 创建一个用户并分配表空间和分配权限
-- generated by: Jeff
-- at: 2016/03/05
-- type: Oracle Database 11g

--创建一个用户Colline_Perf_SingleUserTest，密码为creditderivative
CREATE USER Colline_Perf_SingleUserTest IDENTIFIED BY creditderivative
--设置表空间：让用户使用默认的表空间USERS
DEFAULT TABLESPACE USERS 
--设置表空间：让用户使用临时表空间TEMP
TEMPORARY TABLESPACE TEMP
--默认权限：初始定义对资源不限制
PROFILE DEFAULT
--解锁: oracle的默认设定是登陆时密码错几次之后,系统自动锁定该用户,此时可以用这个命令对该用户解锁
ACCOUNT UNLOCK;
--分配开发人员权利
GRANT RESOURCE TO Colline_Perf_SingleUserTest;
--分配最终用户的典型权利，最基本的
GRANT CONNECT TO Colline_Perf_SingleUserTest;
--分配创建视图的权限
GRANT CREATE VIEW TO Colline_Perf_SingleUserTest;
--将该user已被授权的角色都设置为默认角色
ALTER USER Colline_Perf_SingleUserTest DEFAULT ROLE ALL;
--该user可以在这个表空间内随意建表
GRANT UNLIMITED TABLESPACE TO Colline_Perf_SingleUserTest;
--分配创建物化视图的权限
GRANT CREATE MATERIALIZED VIEW TO Colline_Perf_SingleUserTest;
 
